       const write = async (ctx) => {
    const { title, body, tags } = ctx.request.body;

    const post = new Post({
        title,
        body,
        tags,
    });
    try {
        await post.save();
        ctx.body = post;
    } catch (error) {
        ctx.throw(500, error);
    }
};

       const read = async (ctx) => {
    const { id } = ctx.params;

    try {
        const post = await Post.findById(id).exec();
        if (!post) {
            ctx.status = 404;
            ctx.body = {
                message: 'ポストが存在しません',
            };
            return;
        }
        ctx.body = post;
    } catch (error) {
        ctx.throw(500, error);
    }
    // const post = posts.find((p) => p.id.toString() === id);
};

       const list = async (ctx) => {
    try {
        const posts = await Post.find().exec();
        ctx.body = posts;
    } catch (error) {
        ctx.throw(500, error);
    }
};

       const remove = async (ctx) => {
    const { id } = ctx.params;
    // const index = posts.findIndex((p) => p.id.toString() === id);

    try {
        await Post.findByIdAndRemove(id).exec();
        ctx.status = 204;
    } catch (error) {
        ctx.throw(500, error);
    }

    // posts.splice(index, 1);
    // ctx.status = 204;
};

